import axios from 'axios';
import React, { useEffect, useState } from 'react';

const Base = () => {
  const [user, setUser] = useState([])


  useEffect(() => {
    getUsers()
  },[])
  // https://reqres.in/api/users

function getUsers() {
  try {
    axios.get(`https://reqres.in/api/users`) 
    .then((res) =>  setUser(res.data.data))
  } catch (error) {
    
  }
}

function Delate(id) {
  try {
    axios.delete(`https://reqres.in/api/users/${id}`)
    .then((res) => setUser(user.filter((item)=> item.id !== id)))
  } catch (error) {
    
  }
}




  return (
    <div className='container'>
<button type="button" class="btn btn-primary my-3" data-toggle="modal" data-target="#myModal">
  Add+
</button>


<div class="modal" id="myModal">
  <div class="modal-dialog">
    <div class="modal-content">

   
      <div class="modal-header">
        <h4 class="modal-title">Modal Heading</h4>
       
      </div>

   
      <div class="modal-body">
        
      <div class="form-group">
  <label for="usr">Name:</label>
  <input type="text" class="form-control" id="usr"/>
</div>
<div class="form-group">
  <label for="pwd">Laste:</label>
  <input type="password" class="form-control" id="pwd"/>
</div>
<div class="form-group">
  <label for="pwd">Password:</label>
  <input type="password" class="form-control" id="pwd"/>
</div>
      </div>

     
      <div class="modal-footer">
        <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
      </div>

    </div>
  </div>
</div>


        <table className='table'>
    <thead>
      <tr>  
        <th>№</th>
        <th>Lastname</th>
        <th>Firstname</th>
        <th>Email</th>
        <th>edit</th>
        <th>delate</th>
      </tr>
    </thead>
    <tbody>
    {user.map((piple,index) => ( 
      <tr key={piple.id}>
        <td>{index + 1}</td>
        <td>{piple.last_name}</td>
        <td>{piple.first_name}</td>
        <td>{piple.email}</td>
        <td><button className='btn btn-info'>Edit</button></td>
        <td><button className='btn btn-danger' onClick={() => Delate(piple.id)}> Delate</button></td>
      </tr>
    ))}
    </tbody>
  </table>
    </div>
  );
};

export default Base;
